/**
 * User Model
 */
var clout = require('clout-js'),
	Sequelize = clout.Sequelize,
	sequelize = clout.sequelize;

var definition = {
		user_id: { type: Sequelize.INTEGER, allowNull: false },
		value: { type: Sequelize.STRING, allowNull: false }
	},
	props = {};

module.exports = sequelize.define('Session', definition, props);